import emailjs from '@emailjs/browser';

const SERVICE_ID = 'service_kjwa94s';
const TEMPLATE_ID = 'template_wdqmcgg';
const PUBLIC_KEY = 'x-IkhulHmUhyILb5a';

emailjs.init(PUBLIC_KEY);

export const sendEmail = async (templateParams) => {
  try {
    const response = await emailjs.send(SERVICE_ID, TEMPLATE_ID, templateParams);
    console.log('Email sent successfully!', response.status, response.text);
    return { success: true, response };
  } catch (error) {
    console.error('Failed to send email:', error);
    return { success: false, error };
  }
};

export const generateHtmlTable = (data) => {
  if (!data || data.length === 0) {
    return '<p>No hay datos para mostrar en la tabla.</p>';
  }

  const headers = [
    'Departamento',
    'Grupo de asistencia',
    'Nombre',
    'Fecha',
    'Hora real del registro de entrada',
    'Hora real de registro de salida',
    'Grabaci贸n de asistencia',
    'Duraci贸n de la pausa',
    'Registros de descansos',
    'Periodo de tiempo'
  ];

  let tableHtml = `
    <style>
      table {
        width: 100%;
        border-collapse: collapse;
        margin-bottom: 20px;
        font-family: Arial, sans-serif;
        font-size: 14px;
      }
      th, td {
        border: 1px solid #dddddd;
        text-align: left;
        padding: 8px;
      }
      th {
        background-color: #f2f2f2;
        font-weight: bold;
      }
      tr:nth-child(even) {
        background-color: #f9f9f9;
      }
    </style>
    <table>
      <thead>
        <tr>
  `;

  headers.forEach(header => {
    tableHtml += `<th>${header}</th>`;
  });

  tableHtml += `
        </tr>
      </thead>
      <tbody>
  `;

  data.forEach(row => {
    tableHtml += '<tr>';
    headers.forEach(header => {
      tableHtml += `<td>${row[header] || ''}</td>`;
    });
    tableHtml += '</tr>';
  });

  tableHtml += `
      </tbody>
    </table>
  `;

  return tableHtml;
};
